package com.mycompany.study.ch04;

public class Exif3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 0;
		
		if(a !=0) {
		if (a%2 == 0) {
			System.out.println("입력하신 " + a + "는 짝수입니다");
		} else {
			System.out.println("입력하신 " + a + "는 홀수입니다");
		} 
		
		}else {
			System.out.println("0은 홀짝을 판단할수없습니다");
		}
		
	
	}
}
